mapper.update:
update admin
set admin_name = #{adminName,jdbcType=VARCHAR},
  admin_psw = #{adminPsw,jdbcType=VARCHAR},
  admin_rank = #{adminRank,jdbcType=INTEGER}
where admin_id = #{adminId,jdbcType=INTEGER}

mapper.select:
select admin_id, admin_name, admin_psw, admin_rank
from admin
where admin_id = #{adminId,jdbcType=INTEGER}
